/*Table structure for table `hosregister` */

DROP TABLE IF EXISTS `hosregister`;

CREATE TABLE `hosregister` (
  `hosr_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '挂号编号 也是病人编号',
  `hosr_name` varchar(20) DEFAULT NULL COMMENT '患者姓名',
  `hosr_idcar` varchar(50) DEFAULT NULL COMMENT '身份证',
  `hosr_medical` varchar(50) DEFAULT NULL COMMENT '社保号',
  `hosr_regprice` double DEFAULT NULL COMMENT '挂号费',
  `hosr_phone` varchar(30) DEFAULT NULL COMMENT '联系电话',
  `hosr_selfprice` int(11) DEFAULT NULL COMMENT '是否自费 0自费 1用社保',
  `hosr_sex` char(5) DEFAULT NULL COMMENT '性别',
  `hosr_age` int(11) DEFAULT NULL COMMENT '年龄',
  `hosr_work` varchar(50) DEFAULT NULL COMMENT '职业',
  `hosr_lookdoctor` int(50) DEFAULT NULL COMMENT '初复诊0初诊1复诊',
  `keshi` varchar(50) DEFAULT NULL COMMENT '科室 ',
  `d_id` int(11) DEFAULT NULL COMMENT '医生编号',
  `hosr_remark` varchar(50) DEFAULT NULL COMMENT '备注',
  `hosr_time` datetime DEFAULT NULL COMMENT '挂号时间',
  `hosr_state` int(11) DEFAULT NULL COMMENT '挂号状态0挂号1已住院2已结算3已退号4已出院5已退院',
  PRIMARY KEY (`hosr_id`),
  KEY `did` (`d_id`),
  CONSTRAINT `did` FOREIGN KEY (`d_id`) REFERENCES `doctor` (`d_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=110047 DEFAULT CHARSET=utf8;

